graphql.query:
mutation UpdateAddress($addressId: ID!, $input: UpdateAddressInput!) {
  updateAddress(addressId: $addressId, input: $input) {
    _id
    addresses {
      _id
      address
      city
      country
      postalCode
      phoneNumber
      isDefault
      label
      updatedAt
    }
  }
}

mutation SetDefaultAddress($addressId: ID!) {
  setDefaultAddress(addressId: $addressId) {
    _id
    addresses {
      _id
      address
      isDefault
    }
  }
}

mutation DeleteAddress($addressId: ID!) {
  deleteAddress(addressId: $addressId) {
    _id
    addresses {
      _id
      address
      city
    }
  }
}

query MyFiles($page: Float, $limit: Float) {
  myFiles(page: $page, limit: $limit) {
    files {
      _id
      originalName
      filename
      url
      mimetype
      size
      type
      status
      optimizedUrl
      createdAt
    }
    total
    page
    limit
    totalPages
  }
}

mutation DeleteFile($id: ID!) {
  deleteFile(id: $id)
}

